import { useNavigate } from 'react-router-dom';
import React, { useState, useEffect } from 'react';
import './LoginPage.css';

function LoginPage() {
  const [fetchedData, setFetchedData] = useState({});
  const [newData, setNewData] = useState({
    name: '',
    description: '',
    status: ''
  });
  const navigate = useNavigate();

  // Fetch initial data on component mount
  useEffect(() => {
    fetch('/home/management/checkout/124')
      .then(response => response.json())
      .then(result => setFetchedData(result))
      .catch(error => console.error('Error fetching data:', error));
  }, []);

  // Handle form input changes
  const handleChange = (e) => {
    setNewData({ ...newData, [e.target.name]: e.target.value });
  };

  // Function to send data to the backend
  const handleSubmit = async (e) => {
    e.preventDefault();

    try {
      const response = await fetch('/home/management/checkout/124', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(newData),  // Convert form data to JSON
      });

      const result = await response.json();
      if (response.ok) {
        console.log('Success:', result);
        alert(result.message);
      } else {
        console.error('Error:', result);
        alert(result.error || 'Failed to send data');
      }
    } catch (error) {
      console.error('Error:', error);
      alert('Error sending data');
    }
  };

  function goToAboutPage() {
    navigate('/home');
  }

  return (
    <div className="login-container">
      <div className="login-box">
        <h2>Project ID: {fetchedData.projectID}</h2>
        <p>Name: {fetchedData.name}</p>
        <p>Description: {fetchedData.description}</p>
        <p>Status: {fetchedData.status}</p>

        {/* Form to send new data to the backend */}
        <form onSubmit={handleSubmit}>
          <div className="login-field">
            <label>New Name:</label>
            <input type="text" name="name" value={newData.name} onChange={handleChange} />
          </div>
          <div className="login-field">
            <label>New Description:</label>
            <input type="text" name="description" value={newData.description} onChange={handleChange} />
          </div>
          <div className="login-field">
            <label>New Status:</label>
            <input type="text" name="status" value={newData.status} onChange={handleChange} />
          </div>
          <button type="submit" className="login-button">Update Project</button>
        </form>
      </div>
      <button onClick={goToAboutPage}>Go To Home Page</button>
    </div>
  );
}

export default LoginPage;
